        onSubmit = { addTodo }
        >
            <input 
            style = { input }
            type = "text" 
            name = "title" 
            placeholder = "Add a Todo..." 
            value = {this.state.title}
            onChange = {this.changeTitle}
            />
            <input
            className = "btn"
            style = { btn }
            type = "submit"
            value = "Submit" 
            />
        </form>
    )
  }
}

export default Input
