import React, { Component } from 'react'

class Input extends Component {
  addTodo = () => {
    event.preventDefault()  
  }
  render () {
    const btn = {
        flex: "1"
    }

    const input = {
        flex: "10"
    }
    return (
        <form 
        style = {{ display:"flex" }}
        onSubmit = { addTodo }
        >
            <input 
            style = { input }
            type = "text" 
            name = "title" 
            placeholder = "Add a Todo..." 
            />
            <input
            className = "btn"
            style = { btn }
            type = "submit"
            value = "Submit" 
            />
        </form>
    )
  }
}

export default Input
